mapper.select:
SELECT	 cafe_Name	
FROM cafe 
WHERE cafe_Name = #{value}

SELECT	 url	
FROM cafe 
WHERE url = #{value}

mapper.update:
UPDATE cafe
cafe_name = #{cafeName:VARCHAR} , cafe_Detail = #{cafeDetail:VARCHAR}, banner_img = #{bannerImg:VARCHAR}, main_img = #{mainImg:VARCHAR}, cafe_type = #{cafeType:VARCHAR}, auto_application_accept_flag = #{autoApplicationAcceptFlag:CHAR}, cafe_icon = #{cafeIcon:VARCHAR}
WHERE cafe_no = #{cafeNo:NUMERIC}

UPDATE cafe
auto_application_accept_flag = #{autoApplicationAcceptFlag:CHAR} , member_nickname_flag = #{memberNicknameFlag:CHAR}, application_question1 = #{applicationQuestion1:VARCHAR}, application_question2 = #{applicationQuestion2:VARCHAR}, application_question3 = #{applicationQuestion3:VARCHAR}
WHERE cafe_no = #{cafeNo:NUMERIC}

 	UPDATE cafe
 	
 		close_reason=#{closeReason},
 		closed_flag=#{closedFlag}
 	
	 WHERE cafe_no = #{cafeNo:NUMERIC}

