mapper.select:
	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  					FROM		(
	
	) inner_table
	WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE cafe_member
notice_flag=#{noticeFlag}, member_nickname=#{memberNickname}, favorite_flag=#{favoriteFlag},
WHERE member_no = #{memberNo}
