mapper.sql:
SELECT p.post_no, p.cafe_url, c.cafe_name, c.cafe_icon, p.board_no, p.board_name, p.member_no, p.member_nickname, p.post_title, TO_CHAR(p.reg_date, 'YYYY-MM-DD HH24:MI:SS') AS reg_date, p.view_count, p.like_count, p.post_status_flag, p.post_notice_flag, p.notice_index FROM post p, board b, cafe c, reply r
c.cafe_no = b.cafe_no AND p.board_no = b.board_no AND p.post_status_flag = '0' AND p.post_no = r.post_no(+)
AND p.cafe_url = #{cafeURL}
AND p.reg_date >= #{termStart}
AND p.reg_date <= #{termEnd}
AND p.board_no = #{boardNo}
AND (p.member_nickname like '%${searchKeyword}%' OR p.post_title like '%${searchKeyword}%' OR p.post_content like '%${searchKeyword}%' OR r.reply_content like '%${searchKeyword}%')
AND p.member_nickname like '%${searchKeyword}%'
AND p.post_title like '%${searchKeyword}%'
AND p.post_content like '%${searchKeyword}%'
AND r.reply_content like '%${searchKeyword}%'
GROUP BY p.post_no, p.cafe_url, c.cafe_name, c.cafe_icon, p.board_no, p.board_name, p.member_no, p.member_nickname, p.post_title, p.reg_date, p.view_count, p.like_count, p.post_status_flag, p.post_notice_flag, p.notice_index
ORDER BY p.reg_date DESC

SELECT post_no, cafe_url, board_no, board_name, member_no, member_nickname, post_title, post_content, TO_CHAR(reg_date, 'YYYY-MM-DD HH24:MI:SS') AS reg_date, view_count, like_count, post_status_flag, post_notice_flag, notice_index FROM post
post_status_flag = '0' AND member_no = #{memberNo}
AND reg_date >= #{termStart}
AND reg_date <= #{termEnd}
AND board_no = #{boardNo}
AND (post_title like '%${searchKeyword}%' OR post_content like '%${searchKeyword}%')
AND post_title like '%${searchKeyword}%'
AND post_content like '%${searchKeyword}%'
ORDER BY reg_date DESC

mapper.select:
SELECT
	*
FROM
(SELECT
 inner_table.*, ROWNUM AS row_seq
 FROM 
 () inner_table
 
 	ROWNUM <= #{endRowNum}
 )

	row_seq BETWEEN #{startRowNum} AND #{endRowNum}

