 	AND ROWNUM <= #{endRowNum}
 )

	row_seq BETWEEN #{startRowNum} AND #{endRowNum}

update.foreach:
UPDATE 
	post
SET 
	notice_index  = #{item.noticeIndex }
WHERE 
	post_no = #{item.postNo	}

