mapper.sql:
SELECT p.post_no, p.cafe_url, c.cafe_name, c.cafe_icon, p.board_no, p.board_name, p.member_no, p.member_nickname, p.post_title, p.post_content, TO_CHAR(p.reg_date, 'YYYY-MM-DD HH24:MI:SS') AS reg_date, p.view_count, p.like_count, p.post_status_flag, p.post_notice_flag, p.notice_index FROM post p, board b, cafe c
c.cafe_no = b.cafe_no AND p.board_no = b.board_no AND p.post_status_flag = '0'
AND p.cafe_url = #{cafeURL}
AND p.reg_date >= #{termStart}
AND p.reg_date <= #{termEnd}
AND p.board_no = #{boardNo}
AND (p.member_nickname like '%${searchKeyword}%' OR p.post_title like '%${searchKeyword}%' OR p.post_content like '%${searchKeyword}%')
AND p.member_nickname like '%${searchKeyword}%'
AND p.post_title like '%${searchKeyword}%'
AND p.post_content like '%${searchKeyword}%'
ORDER BY p.reg_date DESC

mapper.select:
SELECT
	*
FROM
(SELECT
 inner_table.*, ROWNUM AS row_seq
 FROM 
 () inner_table
 
 	ROWNUM <= #{endRowNum}
 )

	row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT
	*
FROM
(SELECT
 inner_table.*, ROWNUM AS row_seq
 FROM 
	 () inner_table
 
 	AND ROWNUM <= #{endRowNum}
 )

	row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT
	*
FROM
	(SELECT
	 inner_table.*, ROWNUM AS row_seq
	 FROM 
		 (
		 ) inner_table
		  
 				AND ROWNUM <= #{endRowNum}
		  )

	row_seq BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE
	reply
SET
	reply_content = #{replyContent}
WHERE
	reply_no = #{replyNo}
