mapper.sql:
SELECT post_no, board_no, member_no, post_title, post_content, TO_CHAR(reg_date, 'YYYY-MM-DD HH24:MM:SS') AS reg_date, view_count, like_count, member_nickname, post_status_flag, post_notice_flag, notice_index FROM post
cafe_url = #{cafeURL}
AND reg_date >= #{termStart}
AND reg_date <= #{termEnd}
AND board_no = #{boardNo}
AND (member_nickname like '%${searchKeyword}%' OR post_title like '%${searchKeyword}%' OR post_content like '%${searchKeyword}%')
AND member_nickname like '%${searchKeyword}%'
AND post_title like '%${searchKeyword}%'
AND post_content like '%${searchKeyword}%'
ORDER BY reg_date DESC

mapper.select:
SELECT
*
FROM
(SELECT
 inner_table.*, ROWNUM AS row_seq
 FROM 
 () inner_table
 
 	ROWNUM <= #{endRowNum}
 )

	row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT
*
FROM
