mapper.update:
UPDATE users
user_name = #{userName:VARCHAR} , user_nickname = #{userNickname:VARCHAR}, password = #{password:VARCHAR}, email = #{email:VARCHAR}, phone = #{phone:NUMERIC}, profile_img = #{profileImg:VARCHAR}
WHERE user_no = #{userNo:NUMERIC}

mapper.delete:
DELETE
	account
WHERE
	user_no = #{userNo}
	AND account_type = #{accountType}
	AND account_id = #{accountId}
